import React from 'react';
import { BrowserRouter as Link } from 'react-router-dom';
import itemsJson from '../_data/items.json';
import StorePrices from './StorePrices';

class ItemDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            itemData: itemsJson
        }
    }
    render() { 
        const item = this.state.itemData.find(itemArray=> {
            return itemArray.itemName === this.props.match.params.name
        })
        return (
            <div className="details">

                <img src={require(`../_img/${item.image}`)} alt=""></img>

                <h1 class="h1">{item.itemName}</h1>
                {/*it would be cool if this were a map so it would display all the stores the json had data for*/}
                <StorePrices storeName={item.prices.safeway.storeName}/> 

          </div>
        );
    }

}

export default ItemDetail;
